import React, { useState, useRef } from "react";

export default function CompressImage() {
  const [files, setFiles] = useState([]);
  const [isCompressing, setIsCompressing] = useState(false);
  const dropRef = useRef();

  const handleFilesSelected = (selected) => {
    const validImages = Array.from(selected).filter((file) =>
      file.type.startsWith("image/")
    );

    const fileObjs = validImages.map((file) => ({
      file,
      preview: URL.createObjectURL(file),
      size: (file.size / 1024).toFixed(2),
      progress: 0,
      downloadUrl: null,
      success: false,
      error: null,
    }));

    setFiles(fileObjs);
  };

  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dropRef.current.classList.remove("border-blue-500");
    handleFilesSelected(e.dataTransfer.files);
  };

  const compressFile = async (fileObj, index) => {
    const formData = new FormData();
    formData.append("file", fileObj.file);

    try {
      const res = await fetch("http://localhost:5000/image-compress", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Compression failed");
      const data = await res.json();

      setFiles((prev) => {
        const updated = [...prev];
        updated[index].downloadUrl = "http://localhost:5000" + data.download_url;
        updated[index].success = true;
        updated[index].progress = 100;
        return updated;
      });
    } catch (err) {
      setFiles((prev) => {
        const updated = [...prev];
        updated[index].error = "Compression failed";
        return updated;
      });
    }
  };

  const handleCompressAll = async () => {
    if (!files.length) return alert("Please select images first.");
    setIsCompressing(true);

    for (let i = 0; i < files.length; i++) {
      setFiles((prev) => {
        const updated = [...prev];
        updated[i].progress = 50; // fake mid-progress
        return updated;
      });
      await compressFile(files[i], i);
    }

    setIsCompressing(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-4xl mx-auto text-center">
        <h1 className="text-4xl font-extrabold text-blue-700 mb-4">
          Compress Images
        </h1>
        <p className="text-gray-600 mb-10">
          Upload multiple images and compress them all at once — fast & secure.
        </p>

        {/* Upload area */}
        <div
          ref={dropRef}
          onDragOver={(e) => {
            e.preventDefault();
            e.stopPropagation();
            dropRef.current.classList.add("border-blue-500");
          }}
          onDragLeave={(e) => {
            e.preventDefault();
            e.stopPropagation();
            dropRef.current.classList.remove("border-blue-500");
          }}
          onDrop={onDrop}
          className="border-2 border-dashed border-gray-300 rounded-xl p-10 mb-6 transition hover:border-blue-400 cursor-pointer"
          onClick={() => document.getElementById("imageInput").click()}
        >
          <input
            type="file"
            id="imageInput"
            accept="image/*"
            multiple
            onChange={(e) => handleFilesSelected(e.target.files)}
            className="hidden"
          />
          {!files.length ? (
            <p className="text-gray-500">
              Drag & drop your images here or{" "}
              <span className="text-blue-600 font-medium">browse</span>
            </p>
          ) : (
            <p className="text-gray-600">{files.length} image(s) selected</p>
          )}
        </div>

        {/* File list */}
        <div className="space-y-4 mb-6 text-left">
          {files.map((f, i) => (
            <div
              key={i}
              className="flex items-center gap-4 bg-gray-50 p-4 rounded-lg shadow-sm"
            >
              <img
                src={f.preview}
                alt="preview"
                className="w-16 h-16 object-cover rounded-md border"
              />
              <div className="flex-1">
                <p className="font-medium text-gray-700">{f.file.name}</p>
                <p className="text-sm text-gray-500">{f.size} KB</p>

                {f.error && (
                  <p className="text-sm text-red-600">{f.error}</p>
                )}

                {f.progress > 0 && (
                  <div className="w-full bg-gray-200 rounded-full h-3 mt-2 overflow-hidden">
                    <div
                      className={`h-3 rounded-full ${
                        f.success ? "bg-green-600" : "bg-blue-600"
                      }`}
                      style={{ width: `${f.progress}%` }}
                    ></div>
                  </div>
                )}
              </div>
              {f.downloadUrl && (
                <a
                  href={f.downloadUrl}
                  download
                  className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 text-sm"
                >
                  Download
                </a>
              )}
            </div>
          ))}
        </div>

        {/* Compress button */}
        {!isCompressing && files.length > 0 && (
          <button
            onClick={handleCompressAll}
            className="px-8 py-3 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition"
          >
            Compress All
          </button>
        )}

        {isCompressing && (
          <p className="text-blue-600 font-semibold">Compressing images...</p>
        )}
      </div>
    </div>
  );
}
