import React, { useState, useRef } from "react";

export default function CompressPdf() {
  const [file, setFile] = useState(null);
  const [originalSize, setOriginalSize] = useState(null);
  const [compressedSize, setCompressedSize] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const dropRef = useRef();

  const handleFileUpload = (uploaded) => {
    if (uploaded && uploaded.type === "application/pdf") {
      setFile(uploaded);
      setOriginalSize((uploaded.size / 1024).toFixed(2));
      setCompressedSize(null);
      setDownloadUrl(null);
      setError(null);
    } else {
      setError("Please upload a valid PDF file.");
    }
  };

  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dropRef.current.classList.remove("border-blue-500");
    handleFileUpload(e.dataTransfer.files[0]);
  };

  const handleCompress = async () => {
    if (!file) return setError("Please select a PDF file first.");

    setIsLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/compress-pdf", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();

      if (data.status === "success") {
        setCompressedSize(data.compressed_size_kb);
        setDownloadUrl("http://localhost:5000" + data.download_url);
      } else {
        setError(data.message || "Compression failed");
      }
    } catch (err) {
      console.error(err);
      setError("Compression failed. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-extrabold text-blue-700 mb-4">
          Compress PDF
        </h1>
        <p className="text-gray-600 mb-10">
          Reduce your PDF file size instantly without losing quality — fast,
          secure & free.
        </p>

        <div className="bg-white p-10 rounded-2xl shadow-xl border border-gray-100">
          {/* Drag & drop area */}
          <div
            ref={dropRef}
            onDragOver={(e) => {
              e.preventDefault();
              e.stopPropagation();
              dropRef.current.classList.add("border-blue-500");
            }}
            onDragLeave={(e) => {
              e.preventDefault();
              e.stopPropagation();
              dropRef.current.classList.remove("border-blue-500");
            }}
            onDrop={onDrop}
            className="border-2 border-dashed border-gray-300 rounded-xl p-10 mb-6 transition hover:border-blue-400 cursor-pointer"
            onClick={() => document.getElementById("fileInput").click()}
          >
            <input
              type="file"
              id="fileInput"
              accept=".pdf"
              onChange={(e) => handleFileUpload(e.target.files[0])}
              className="hidden"
            />
            {!file ? (
              <p className="text-gray-500">
                Drag & drop your PDF here or <span className="text-blue-600 font-medium">browse</span>
              </p>
            ) : (
              <div className="text-gray-700">
                <p className="font-semibold">{file.name}</p>
                <p className="text-sm">{originalSize} KB</p>
              </div>
            )}
          </div>

          {/* Compress Button */}
          {!isLoading && file && (
            <button
              onClick={handleCompress}
              className="px-8 py-3 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition"
            >
              Compress PDF
            </button>
          )}

          {/* Loading Progress */}
          {isLoading && (
            <div className="mt-6">
              <div className="text-blue-600 font-semibold mb-4">
                Compressing...
              </div>
              <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden mb-2">
                <div className="bg-blue-600 h-4 rounded-full animate-pulse w-full"></div>
              </div>
              <p className="text-gray-500 text-sm">Please wait...</p>
            </div>
          )}

          {/* Error */}
          {error && (
            <div className="mt-4 text-red-600 font-medium">{error}</div>
          )}

          {/* Results */}
          {compressedSize && (
            <div className="mt-6 text-green-600 font-bold">
              ✅ Compressed size: {compressedSize} KB
            </div>
          )}

          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl font-semibold hover:bg-green-700 transition"
            >
              Download Compressed PDF
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
