import React, { useState, useRef } from "react";

export default function WordToExcel() {
  const [file, setFile] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [successMsg, setSuccessMsg] = useState("");
  const dropRef = useRef();

  const handleFile = (selected) => {
    const f = selected[0];
    if (f && (f.type === "application/msword" || f.name.endsWith(".docx"))) {
      setFile(f);
      setDownloadUrl(null);
      setSuccessMsg("");
    } else {
      alert("Please upload a valid Word (.doc or .docx) file");
    }
  };

  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dropRef.current.classList.remove("border-blue-500");
    handleFile(e.dataTransfer.files);
  };

  const handleConvert = async () => {
    if (!file) return alert("Please select a Word file first");
    setIsLoading(true);
    setSuccessMsg("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/word-to-excel", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Conversion failed");
      const data = await res.json();

      setDownloadUrl("http://localhost:5000" + data.download_url);
      setSuccessMsg("Converted Successfully ✅");
    } catch (err) {
      console.error(err);
      alert("Conversion failed");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-extrabold text-blue-700 mb-4">
          Word to Excel
        </h1>
        <p className="text-gray-600 mb-10">
          Convert your Word documents (.doc / .docx) to Excel sheets instantly.
        </p>

        <div className="bg-white p-10 rounded-2xl shadow-lg">

          {/* Upload Box */}
          <div
            ref={dropRef}
            onDragOver={(e) => {
              e.preventDefault();
              dropRef.current.classList.add("border-blue-500");
            }}
            onDragLeave={(e) => {
              e.preventDefault();
              dropRef.current.classList.remove("border-blue-500");
            }}
            onDrop={onDrop}
            className="border-2 border-dashed border-gray-300 rounded-xl p-10 mb-6 transition hover:border-blue-400 cursor-pointer"
            onClick={() => document.getElementById("wordInput").click()}
          >
            <input
              type="file"
              id="wordInput"
              accept=".doc,.docx"
              onChange={(e) => handleFile(e.target.files)}
              className="hidden"
            />
            {!file ? (
              <p className="text-gray-500">
                Drag & drop your Word file here or{" "}
                <span className="text-blue-600 font-medium">browse</span>
              </p>
            ) : (
              <p className="text-gray-700 font-medium">{file.name}</p>
            )}
          </div>

          {/* Convert Button */}
          {!isLoading && file && (
            <button
              onClick={handleConvert}
              className="px-8 py-3 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition"
            >
              Convert to Excel
            </button>
          )}

          {/* Loading */}
          {isLoading && (
            <div className="mt-4">
              <div className="text-blue-600 font-semibold mb-3">Converting...</div>
              <div className="w-full bg-gray-200 rounded-full h-4">
                <div className="bg-blue-600 h-4 rounded-full animate-pulse w-full"></div>
              </div>
            </div>
          )}

          {/* Success */}
          {successMsg && (
            <div className="mt-4 text-green-600 font-semibold">{successMsg}</div>
          )}

          {/* Download Button */}
          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700"
            >
              Download Excel File
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
