import React, { useState } from "react";

export default function ExcelToWord() {
  const [file, setFile] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [successMsg, setSuccessMsg] = useState(""); // ✅ NEW

  const handleConvert = async () => {
    if (!file) return alert("Select an Excel file first");
    setIsLoading(true);
    setSuccessMsg("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/excel-to-word", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Conversion failed");

      const data = await res.json();
      setDownloadUrl("http://localhost:5000" + data.download_url);
      setSuccessMsg("Converted Successfully ✅"); // ✅ show success
    } catch (err) {
      console.error(err);
      alert("Conversion failed");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-4">Excel to Word</h1>
        <p className="text-gray-600 mb-10">
          Convert your Excel sheets to Word format.
        </p>

        <div className="bg-white p-8 rounded-2xl shadow-lg">
          <input
            type="file"
            accept=".xls,.xlsx"
            onChange={(e) => {
              setFile(e.target.files[0]);
              setSuccessMsg("");
              setDownloadUrl(null);
            }}
            className="block mx-auto mb-6"
          />

          {file && !isLoading && (
            <button
              onClick={handleConvert}
              className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700"
            >
              Convert
            </button>
          )}

          {isLoading && (
            <div className="text-blue-600 font-semibold mb-2">Converting...</div>
          )}

          {/* ✅ Success message */}
          {successMsg && (
            <div className="mt-4 text-green-600 font-semibold">{successMsg}</div>
          )}

          {/* ✅ Download Button */}
          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700"
            >
              Download Word
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
