import { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { Menu, X } from "lucide-react";

export default function Navbar() {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [servicesOpen, setServicesOpen] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown if clicked outside
  useEffect(() => {
    function handleClickOutside(e) {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setServicesOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <nav className="fixed top-0 left-0 w-full z-50 backdrop-blur-md bg-white/60 border-b border-white/30 shadow-sm">
      <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Brand */}
        <h1 className="text-2xl font-extrabold text-blue-700 tracking-tight">
          DocuMorph
        </h1>

        {/* Desktop Nav */}
        <div className="hidden md:flex items-center space-x-8 text-gray-700 font-medium">
          <NavLink to="/">Home</NavLink>

          {/* Services Dropdown */}
          <div className="relative" ref={dropdownRef}>
            <button
              onClick={() => setServicesOpen(!servicesOpen)}
              className="flex items-center gap-1 transition-colors duration-300 hover:text-blue-700"
            >
              Services
              <span className="text-xs">{servicesOpen ? "▲" : "▼"}</span>
            </button>

            {servicesOpen && (
              <div className="absolute top-full left-0 mt-3 w-56 bg-white/95 backdrop-blur-md border border-white/30 rounded-xl shadow-lg overflow-hidden">
                <div className="flex flex-col text-gray-700">
                  <DropdownItem to="/compress-pdf" closeMenu={() => setServicesOpen(false)}>Compress PDF</DropdownItem>
                  <DropdownItem to="/compress-image" closeMenu={() => setServicesOpen(false)}>Compress Image</DropdownItem>
                  <DropdownItem to="/word-to-excel" closeMenu={() => setServicesOpen(false)}>Word to Excel</DropdownItem>
                  <DropdownItem to="/excel-to-word" closeMenu={() => setServicesOpen(false)}>Excel to Word</DropdownItem>
                  <DropdownItem to="/pdf-to-word" closeMenu={() => setServicesOpen(false)}>PDF to Word</DropdownItem>
                </div>
              </div>
            )}
          </div>

          <NavLink to="/about">About</NavLink>
          <NavLink to="/contact">Contact</NavLink>
        </div>

        {/* Mobile Hamburger */}
        <button
          className="md:hidden text-gray-700"
          onClick={() => setMobileOpen(!mobileOpen)}
        >
          {mobileOpen ? <X size={28} /> : <Menu size={28} />}
        </button>
      </div>

      {/* Mobile Menu */}
      {mobileOpen && (
        <div className="md:hidden bg-white/90 backdrop-blur-md border-t border-white/30 shadow-lg px-6 py-4 space-y-3 text-gray-700">
          <Link className="block py-2" to="/" onClick={() => setMobileOpen(false)}>
            Home
          </Link>

          {/* Mobile Services Dropdown */}
          <div className="border-t border-gray-200 pt-2">
            <button
              className="w-full flex justify-between items-center py-2"
              onClick={() => setServicesOpen(!servicesOpen)}
            >
              <span>Services</span>
              <span>{servicesOpen ? "▲" : "▼"}</span>
            </button>
            {servicesOpen && (
              <div className="pl-4 flex flex-col space-y-2">
                <Link to="/compress-pdf" onClick={() => setMobileOpen(false)}>Compress PDF</Link>
                <Link to="/compress-image" onClick={() => setMobileOpen(false)}>Compress Image</Link>
                <Link to="/word-to-excel" onClick={() => setMobileOpen(false)}>Word to Excel</Link>
                <Link to="/excel-to-word" onClick={() => setMobileOpen(false)}>Excel to Word</Link>
                <Link to="/pdf-to-word" onClick={() => setMobileOpen(false)}>PDF to Word</Link>
              </div>
            )}
          </div>

          <Link className="block py-2" to="/about" onClick={() => setMobileOpen(false)}>
            About
          </Link>
          <Link className="block py-2" to="/contact" onClick={() => setMobileOpen(false)}>
            Contact
          </Link>
        </div>
      )}
    </nav>
  );
}

// Helper components
function NavLink({ to, children }) {
  return (
    <Link to={to} className="relative group">
      <span className="transition-colors duration-300 group-hover:text-blue-700">
        {children}
      </span>
      <span className="absolute left-0 -bottom-1 w-0 h-0.5 bg-blue-700 transition-all duration-300 group-hover:w-full"></span>
    </Link>
  );
}

function DropdownItem({ to, children, closeMenu }) {
  return (
    <Link
      to={to}
      onClick={closeMenu}
      className="px-4 py-2 hover:bg-blue-50 hover:text-blue-700"
    >
      {children}
    </Link>
  );
}
