import React from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { 
  FileText, FileSpreadsheet, FileArchive, FileImage, FileCode, Scissors, FileDown, FileUp 
} from "lucide-react";

const tools = [
  {
    title: "Compress PDF",
    description: "Reduce the file size of your PDF documents quickly and easily.",
    icon: <FileArchive className="h-10 w-10 text-purple-600" />
  },
  {
    title: "Word → PDF",
    description: "Convert your Word documents into high-quality PDFs.",
    icon: <FileText className="h-10 w-10 text-blue-600" />
  },
  {
    title: "PDF → Word",
    description: "Turn your PDFs back into editable Word documents.",
    icon: <FileText className="h-10 w-10 text-green-600" />
  },
  {
    title: "Excel → PDF",
    description: "Export your spreadsheets into a secure PDF format.",
    icon: <FileSpreadsheet className="h-10 w-10 text-yellow-600" />
  },
  {
    title: "PDF → Excel",
    description: "Extract data tables from PDFs into Excel spreadsheets.",
    icon: <FileSpreadsheet className="h-10 w-10 text-emerald-600" />
  },
  {
    title: "Image → PDF",
    description: "Combine images into a single organized PDF file.",
    icon: <FileImage className="h-10 w-10 text-pink-600" />
  },
  {
    title: "Split PDF",
    description: "Split large PDF files into smaller, more manageable parts.",
    icon: <Scissors className="h-10 w-10 text-red-600" />
  },
  {
    title: "Merge PDF",
    description: "Combine multiple PDFs into one convenient document.",
    icon: <FileCode className="h-10 w-10 text-indigo-600" />
  },
  {
    title: "Download Templates",
    description: "Download ready-made templates for your projects.",
    icon: <FileDown className="h-10 w-10 text-cyan-600" />
  },
  {
    title: "Upload Files",
    description: "Upload files securely to work with our tools.",
    icon: <FileUp className="h-10 w-10 text-orange-600" />
  }
];

const Services = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 to-purple-100 p-8">
      <h1 className="text-4xl font-extrabold text-center text-indigo-800 mb-10">
        Our File Conversion Tools
      </h1>
      <div className="grid gap-8 grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 max-w-6xl mx-auto">
        {tools.map((tool, index) => (
          <Card key={index} className="bg-white shadow-lg rounded-2xl hover:shadow-2xl transition transform hover:-translate-y-1">
            <CardContent className="flex flex-col items-center text-center p-6">
              {tool.icon}
              <h2 className="mt-4 text-xl font-bold text-gray-800">{tool.title}</h2>
              <p className="text-gray-600 mt-2 text-sm">{tool.description}</p>
              <Button className="mt-4 bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-xl">
                Use Tool
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default Services;
