import React, { useState, useRef } from "react";

export default function CompressImage() {
  const [file, setFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [fileSize, setFileSize] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [successMsg, setSuccessMsg] = useState("");
  const [error, setError] = useState("");
  const dropRef = useRef();

  const handleFileUpload = (uploaded) => {
    if (uploaded && uploaded.type.startsWith("image/")) {
      setFile(uploaded);
      setPreviewUrl(URL.createObjectURL(uploaded));
      setFileSize((uploaded.size / 1024).toFixed(2));
      setDownloadUrl(null);
      setSuccessMsg("");
      setError("");
    } else {
      setError("Please upload a valid image file.");
    }
  };

  const onDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    dropRef.current.classList.remove("border-blue-500");
    handleFileUpload(e.dataTransfer.files[0]);
  };

  const handleCompress = async () => {
    if (!file) return setError("Please select an image first.");

    setIsLoading(true);
    setProgress(0);
    setSuccessMsg("");
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/image-compress", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Compression failed");

      const data = await res.json();
      setDownloadUrl("http://localhost:5000" + data.download_url);

      setProgress(100);
      setSuccessMsg("âœ… Compressed Successfully");
    } catch (err) {
      console.error(err);
      setError("Compression failed. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-extrabold text-blue-700 mb-4">
          Compress Image
        </h1>
        <p className="text-gray-600 mb-10">
          Reduce your image file size while preserving quality â€” fast & secure.
        </p>

        <div className="bg-white p-10 rounded-2xl shadow-xl border border-gray-100">
          {/* Upload area */}
          <div
            ref={dropRef}
            onDragOver={(e) => {
              e.preventDefault();
              e.stopPropagation();
              dropRef.current.classList.add("border-blue-500");
            }}
            onDragLeave={(e) => {
              e.preventDefault();
              e.stopPropagation();
              dropRef.current.classList.remove("border-blue-500");
            }}
            onDrop={onDrop}
            className="border-2 border-dashed border-gray-300 rounded-xl p-10 mb-6 transition hover:border-blue-400 cursor-pointer"
            onClick={() => document.getElementById("imageInput").click()}
          >
            <input
              type="file"
              id="imageInput"
              accept="image/*"
              onChange={(e) => handleFileUpload(e.target.files[0])}
              className="hidden"
            />
            {!file ? (
              <p className="text-gray-500">
                Drag & drop your image here or{" "}
                <span className="text-blue-600 font-medium">browse</span>
              </p>
            ) : (
              <div className="flex flex-col items-center gap-2">
                <img
                  src={previewUrl}
                  alt="Preview"
                  className="w-24 h-24 object-cover rounded-md"
                />
                <p className="font-medium text-gray-700">{file.name}</p>
                <p className="text-sm text-gray-500">{fileSize} KB</p>
              </div>
            )}
          </div>

          {/* Compress Button */}
          {!isLoading && file && (
            <button
              onClick={handleCompress}
              className="px-8 py-3 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition"
            >
              Compress Image
            </button>
          )}

          {/* Progress */}
          {isLoading && (
            <div className="mt-6">
              <div className="text-blue-600 font-semibold mb-4">
                Compressing...
              </div>
              <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden mb-2">
                <div
                  className="bg-blue-600 h-4 rounded-full animate-pulse"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
              <p className="text-gray-500 text-sm">Please wait...</p>
            </div>
          )}

          {/* Messages */}
          {error && (
            <div className="mt-4 text-red-600 font-semibold">{error}</div>
          )}
          {successMsg && (
            <div className="mt-4 text-green-600 font-semibold">{successMsg}</div>
          )}

          {/* Download */}
          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl font-semibold hover:bg-green-700 transition"
            >
              Download Compressed Image
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
