import React, { useState } from "react";

export default function WordToExcel() {
  const [file, setFile] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleConvert = async () => {
    if (!file) return alert("Select a Word file first");
    setIsLoading(true);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/word-to-excel", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setDownloadUrl("http://localhost:5000" + data.download_url);
    } catch (err) {
      console.error(err);
      alert("Conversion failed");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-4">Word to Excel</h1>
        <p className="text-gray-600 mb-10">Convert your Word documents to Excel format.</p>

        <div className="bg-white p-8 rounded-2xl shadow-lg">
          <input type="file" accept=".doc,.docx" onChange={(e) => setFile(e.target.files[0])} className="block mx-auto mb-6" />
          {file && !isLoading && (
            <button onClick={handleConvert} className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700">
              Convert
            </button>
          )}
          {isLoading && <div className="text-blue-600">Converting...</div>}
          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700"
            >
              Download Excel
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
