import React, { useState } from "react";

export default function PdfEditor() {
  const [action, setAction] = useState("rotate");
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);
  const [angle, setAngle] = useState(90);
  const [pages, setPages] = useState("");
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleSingle = (e) => setFile(e.target.files[0]);
  const handleMultiple = (e) => setFiles(Array.from(e.target.files));

  const handleSubmit = async () => {
    setIsLoading(true);
    const form = new FormData();

    if (action === "merge") {
      files.forEach(f => form.append("files", f));
    } else {
      form.append("file", file);
      if (action === "rotate") form.append("angle", angle);
      if (action === "split") form.append("pages", pages);
    }
    form.append("action", action);

    try {
      const res = await fetch("http://localhost:5000/pdf-editor", {
        method: "POST",
        body: form
      });
      const data = await res.json();
      setDownloadUrl("http://localhost:5000" + data.download_url);
    } catch (err) {
      console.error(err);
      alert("Operation failed");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen pt-24 px-6">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg p-8">
        <h1 className="text-3xl font-bold text-blue-700 mb-4">PDF Editor</h1>

        <select
          value={action}
          onChange={(e) => setAction(e.target.value)}
          className="border mb-4 p-2 rounded"
        >
          <option value="rotate">Rotate</option>
          <option value="split">Split</option>
          <option value="merge">Merge</option>
        </select>

        {action === "merge" ? (
          <input multiple type="file" accept=".pdf" onChange={handleMultiple} />
        ) : (
          <input type="file" accept=".pdf" onChange={handleSingle} />
        )}

        {action === "rotate" && (
          <input
            type="number"
            value={angle}
            onChange={(e) => setAngle(e.target.value)}
            placeholder="Angle (90, 180)"
            className="border p-2 rounded my-2"
          />
        )}
        {action === "split" && (
          <input
            type="text"
            value={pages}
            onChange={(e) => setPages(e.target.value)}
            placeholder="Pages (e.g. 1,3-5)"
            className="border p-2 rounded my-2"
          />
        )}

        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white px-6 py-2 rounded mt-4"
          disabled={isLoading}
        >
          {isLoading ? "Processing..." : "Submit"}
        </button>

        {downloadUrl && (
          <a
            href={downloadUrl}
            download
            className="block mt-4 bg-green-600 text-white px-6 py-2 rounded text-center"
          >
            Download Edited PDF
          </a>
        )}
      </div>
    </div>
  );
}
