import React, { useState } from "react";

export default function CompressPdf() {
  const [file, setFile] = useState(null);
  const [originalSize, setOriginalSize] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [compressedSize, setCompressedSize] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);

  const handleFileUpload = (e) => {
    const uploaded = e.target.files[0];
    if (uploaded && uploaded.type === "application/pdf") {
      setFile(uploaded);
      setOriginalSize((uploaded.size / 1024).toFixed(2));
      setCompressedSize(null);
      setDownloadUrl(null);
    } else {
      alert("Please upload a valid PDF file.");
    }
  };

  const handleCompress = async () => {
    if (!file) return alert("Please select a PDF file first.");

    setIsLoading(true);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/compress-pdf", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();

      if (data.status === "success") {
        setCompressedSize(data.compressed_size_kb);
        setDownloadUrl("http://localhost:5000" + data.download_url);
      } else {
        alert(data.message || "Compression failed");
      }
    } catch (err) {
      console.error(err);
      alert("Compression failed");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-4">Compress PDF</h1>
        <p className="text-gray-600 mb-10">
          Reduce your PDF file size without losing quality. Fast and secure.
        </p>

        <div className="bg-white p-8 rounded-2xl shadow-lg">
          {!isLoading ? (
            <>
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileUpload}
                className="block mx-auto mb-6"
              />

              {file && (
                <div className="mb-4 text-gray-700">
                  <p><strong>File:</strong> {file.name}</p>
                  <p><strong>Size:</strong> {originalSize} KB</p>
                </div>
              )}

              {file && (
                <button
                  onClick={handleCompress}
                  className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700"
                >
                  Compress PDF
                </button>
              )}
            </>
          ) : (
            <div>
              <div className="text-blue-600 font-semibold mb-4">Compressing...</div>
              <div className="w-full bg-gray-200 rounded-full h-4 mb-2">
                <div
                  className="bg-blue-600 h-4 rounded-full animate-pulse"
                  style={{ width: `100%` }}
                ></div>
              </div>
              <p>Please wait...</p>
            </div>
          )}

          {compressedSize && (
            <div className="mt-6 text-green-600 font-bold">
              ✅ New size: {compressedSize} KB
            </div>
          )}

          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700"
            >
              Download Compressed PDF
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
