import React, { useState } from "react";

export default function PdfToWord() {
  const [file, setFile] = useState(null);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false); // New state variable

  const handleConvert = async () => {
    if (!file) {
      alert("Select a PDF file first");
      return;
    }

    setIsLoading(true);
    setMessage("");
    setDownloadUrl(null);
    setIsSuccess(false); // Reset status

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/pdf-to-word", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      
      if (res.ok) {
        setDownloadUrl("http://localhost:5000" + data.download_url);
        setMessage("Conversion successful! Your file is ready to download.");
        setIsSuccess(true); // Set success state to true
      } else {
        setMessage(data.error || "Conversion failed.");
        setIsSuccess(false); // Set success state to false
      }
    } catch (err) {
      console.error(err);
      setMessage("Conversion failed due to a network error.");
      setIsSuccess(false); // Set success state to false
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-4">PDF to Word</h1>
        <p className="text-gray-600 mb-10">Convert your PDFs to editable Word format.</p>

        <div className="bg-white p-8 rounded-2xl shadow-lg">
          <input
            type="file"
            accept=".pdf"
            onChange={(e) => {
              setFile(e.target.files[0]);
              setMessage("");
              setDownloadUrl(null);
              setIsSuccess(false); // Reset status
            }}
            className="block mx-auto mb-6"
          />
          {file && !isLoading && (
            <button
              onClick={handleConvert}
              className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700"
            >
              Convert
            </button>
          )}
          {isLoading && <div className="text-blue-600 mt-4">Converting...</div>}
          
          {message && (
            <div className={`mt-4 ${isSuccess ? 'text-green-600' : 'text-red-600'}`}>
              {message}
            </div>
          )}

          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700"
            >
              Download Word
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
