import React, { useState } from "react";

export default function CompressImage() {
  const [file, setFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [successMsg, setSuccessMsg] = useState(""); // ✅ NEW

  const handleFileUpload = (e) => {
    const uploaded = e.target.files[0];
    if (uploaded && uploaded.type.startsWith("image/")) {
      setFile(uploaded);
      setDownloadUrl(null);
      setSuccessMsg("");
    }
  };

  const handleCompress = async () => {
    if (!file) return alert("Please select an image");
    setIsLoading(true);
    setProgress(0);
    setSuccessMsg("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await fetch("http://localhost:5000/image-compress", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Compression failed");

      const data = await res.json();
      setDownloadUrl("http://localhost:5000" + data.download_url);

      setProgress(100);
      setSuccessMsg("Compressed Successfully ✅"); // ✅ SUCCESS MESSAGE
    } catch (err) {
      console.error(err);
      alert("Compression failed");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-4">Compress Image</h1>
        <p className="text-gray-600 mb-10">
          Reduce your image size while maintaining quality.
        </p>

        <div className="bg-white p-8 rounded-2xl shadow-lg">
          {!isLoading ? (
            <>
              <input
                type="file"
                accept="image/*"
                onChange={handleFileUpload}
                className="block mx-auto mb-6"
              />
              {file && (
                <button
                  onClick={handleCompress}
                  className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700"
                >
                  Compress Image
                </button>
              )}
            </>
          ) : (
            <div>
              <div className="text-blue-600 font-semibold mb-4">
                Compressing...
              </div>
              <div className="w-full bg-gray-200 rounded-full h-4 mb-2">
                <div
                  className="bg-blue-600 h-4 rounded-full"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
            </div>
          )}

          {/* ✅ Success Message */}
          {successMsg && (
            <div className="mt-4 text-green-600 font-semibold">{successMsg}</div>
          )}

          {/* ✅ Download Button */}
          {downloadUrl && (
            <a
              href={downloadUrl}
              download
              className="mt-4 inline-block px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700"
            >
              Download Compressed Image
            </a>
          )}
        </div>
      </div>
    </div>
  );
}
