import React, { useState } from "react";

export default function CompressPdf() {
  const [file, setFile] = useState(null);
  const [originalSize, setOriginalSize] = useState(null);
  const [targetSize, setTargetSize] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [compressedSize, setCompressedSize] = useState(null);

  const handleFileUpload = (e) => {
    const uploaded = e.target.files[0];
    if (uploaded && uploaded.type === "application/pdf") {
      setFile(uploaded);
      setOriginalSize((uploaded.size / 1024).toFixed(2)); // in KB
      setCompressedSize(null);
    }
  };

  const handleCompress = async () => {
    if (!file || !targetSize) return alert("Please select file and target size");

    setIsLoading(true);
    setProgress(0);

    // fake visual progress bar (optional) while waiting for backend
    const fakeProgress = setInterval(() => {
      setProgress((p) => {
        if (p >= 95) {
          clearInterval(fakeProgress);
          return 95;
        }
        return p + 5;
      });
    }, 300);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("target_size_kb", targetSize);

    try {
      const res = await fetch("http://localhost:5000/compress-pdf", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      clearInterval(fakeProgress);
      setProgress(100);
      setCompressedSize(data.compressed_size_kb);
      setIsLoading(false);
    } catch (err) {
      console.error(err);
      alert("Compression failed");
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white pt-24 px-6">
      <div className="max-w-3xl mx-auto text-center">
        <h1 className="text-4xl font-bold text-blue-700 mb-4">Compress PDF</h1>
        <p className="text-gray-600 mb-10">
          Reduce your PDF file size without losing quality. Fast and secure.
        </p>

        <div className="bg-white p-8 rounded-2xl shadow-lg">
          {!isLoading ? (
            <>
              <p className="text-gray-500 mb-4">üìÅ Upload your PDF file here</p>
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileUpload}
                className="block mx-auto mb-6"
              />

              {file && (
                <div className="mb-4 text-gray-700">
                  <p><strong>File:</strong> {file.name}</p>
                  <p><strong>Size:</strong> {originalSize} KB</p>
                </div>
              )}

              {file && (
                <input
                  type="number"
                  placeholder="Enter target size (KB)"
                  className="border border-gray-300 rounded-lg px-4 py-2 mb-4 w-full"
                  value={targetSize}
                  onChange={(e) => setTargetSize(e.target.value)}
                />
              )}

              <button
                onClick={handleCompress}
                className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 disabled:opacity-50"
                disabled={!file || !targetSize}
              >
                Compress PDF
              </button>
            </>
          ) : (
            <div>
              <div className="text-blue-600 font-semibold mb-4">Compressing...</div>
              <div className="w-full bg-gray-200 rounded-full h-4 mb-2">
                <div
                  className="bg-blue-600 h-4 rounded-full transition-all duration-300"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>
              <p>{progress}%</p>
            </div>
          )}

          {compressedSize && (
            <div className="mt-6 text-green-600 font-bold">
              ‚úÖ Compression complete! New size: {compressedSize} KB
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
