import React, { useState } from 'react';
import axios from 'axios';

// The Home page component with welcome message and elegant styling
const Home = ({ onNavigate }) => {
    return (
        <div className="flex flex-col items-center justify-center p-8 bg-gray-50 rounded-lg shadow-inner">
            <h1 className="text-5xl font-extrabold text-gray-900 mb-4 animate-fade-in-down">
                Welcome to <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-500 to-purple-600">DocuMorph</span>
            </h1>
            <p className="text-xl text-gray-600 max-w-2xl text-center mb-8 animate-fade-in">
                Your one-stop solution to effortlessly transform and manage your documents. Compress PDFs or convert between Word and Excel with ease and precision.
            </p>
            <button 
                onClick={() => onNavigate('services')}
                className="py-3 px-8 rounded-full text-lg font-semibold text-white bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 transition-all duration-300 transform hover:scale-105 shadow-lg"
            >
                Get Started
            </button>
        </div>
    );
};

export default Home;
