import React, { useState } from "react";

export default function PdfEditor() {
  const [action, setAction] = useState("rotate");
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);
  const [angle, setAngle] = useState(90);
  const [pages, setPages] = useState("");
  const [downloadUrl, setDownloadUrl] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSingle = (e) => {
    setFile(e.target.files[0]);
    setError(null);
    setDownloadUrl(null); // Clear previous download link
  };

  const handleMultiple = (e) => {
    setFiles(Array.from(e.target.files));
    setError(null);
    setDownloadUrl(null); // Clear previous download link
  };

  const handleSubmit = async () => {
    setIsLoading(true);
    setError(null);
    const form = new FormData();

    if (action === "merge") {
      if (files.length < 2) {
        setError("Please select at least two files to merge.");
        setIsLoading(false);
        return;
      }
      files.forEach((f) => form.append("files", f));
    } else {
      if (!file) {
        setError("Please select a file.");
        setIsLoading(false);
        return;
      }
      form.append("file", file);
      if (action === "rotate") form.append("angle", angle);
      if (action === "split") form.append("pages", pages);
    }
    form.append("action", action);

    try {
      const res = await fetch("http://localhost:5000/pdf-editor", {
        method: "POST",
        body: form,
      });
      const data = await res.json();
      if (res.ok) {
        setDownloadUrl("http://localhost:5000" + data.download_url);
      } else {
        setError(data.error || "Operation failed.");
      }
    } catch (err) {
      console.error(err);
      setError("An unexpected error occurred.");
    } finally {
      setIsLoading(false);
    }
  };

  const isSubmitDisabled = isLoading || (action !== "merge" && !file) || (action === "merge" && files.length < 2);

  return (
    <div className="min-h-screen pt-24 px-6 bg-gray-100">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg p-8">
        <h1 className="text-3xl font-bold text-blue-700 mb-4">PDF Editor</h1>

        <div className="mb-4">
          <label htmlFor="action-select" className="block text-gray-700 font-medium mb-1">Select Action</label>
          <select
            id="action-select"
            value={action}
            onChange={(e) => {
              setAction(e.target.value);
              setFile(null);
              setFiles([]);
              setDownloadUrl(null);
              setError(null);
            }}
            className="border p-2 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="rotate">Rotate PDF</option>
            <option value="split">Split PDF</option>
            <option value="merge">Merge PDFs</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-1">
            {action === "merge" ? "Select files to merge" : "Select a PDF file"}
          </label>
          {action === "merge" ? (
            <input
              multiple
              type="file"
              accept=".pdf"
              onChange={handleMultiple}
              className="w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
          ) : (
            <input
              type="file"
              accept=".pdf"
              onChange={handleSingle}
              className="w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
          )}
        </div>

        {action === "rotate" && (
          <div className="mb-4">
            <label htmlFor="angle-input" className="block text-gray-700 font-medium mb-1">Rotation Angle</label>
            <input
              id="angle-input"
              type="number"
              value={angle}
              onChange={(e) => setAngle(e.target.value)}
              placeholder="Angle (90, 180, 270)"
              className="border p-2 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        )}
        {action === "split" && (
          <div className="mb-4">
            <label htmlFor="pages-input" className="block text-gray-700 font-medium mb-1">Pages to Keep</label>
            <input
              id="pages-input"
              type="text"
              value={pages}
              onChange={(e) => setPages(e.target.value)}
              placeholder="e.g. 1,3-5"
              className="border p-2 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        )}

        <button
          onClick={handleSubmit}
          className={`w-full px-6 py-3 rounded-lg font-semibold text-white transition-colors duration-200 ${
            isSubmitDisabled ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
          }`}
          disabled={isSubmitDisabled}
        >
          {isLoading ? "Processing..." : "Submit"}
        </button>

        {error && (
          <div className="mt-4 p-4 bg-red-100 text-red-700 rounded-lg">
            <p>{error}</p>
          </div>
        )}

        {downloadUrl && (
          <a
            href={downloadUrl}
            download
            className="block mt-4 bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg text-center font-semibold transition-colors duration-200"
          >
            Download Edited PDF
          </a>
        )}
      </div>
    </div>
  );
}
